import { creatGlobalStyle } from 'styled-components'

export const GlobalStyle = creatGlobalStyle`
body {
    font-family: 'Open Sans Condensed';
    padding: 20px 40px;
  }
  
  a {
    text-decoration: none;
    color: black;
  }
  
  * {
    box-sizing: border-box;
    font-family: "Comic Neue";
  }
  
`
